<section class=form_wrap><form action=/layout/x-send-mail.php method=post><input name=name type=text class=feedback-input placeholder=Name pattern=([A-Za-z\s]){2,48} required><input name=email type=email class=feedback-input placeholder=Email pattern=[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$ required><input name=phone type=tel class=feedback-input placeholder=Teléfono pattern=([0-9\s]){10,14} required><textarea name=message class=feedback-input max-length=255 placeholder="Mensaje :)"></textarea><input type=submit value="Enviar Mensaje"></form></section>